mapper.select:
      SELECT
	    longitude_latitude AS lonlat,
	    times_tamp AS timestamp
      FROM
	    t_background_locate_user

      WHERE
	    user_id = #{unitId,jdbcType=INTEGER}
        AND times_tamp >= #{startTimestamp,jdbcType=INTEGER}
        AND times_tamp <= #{endTimestamp,jdbcType=INTEGER}
